{-# LANGUAGE OverloadedStrings #-}import Network.Wai (Application, responseLBS)import Network.Wai.Handler.Warp (run)import Network.HTTP.Types (status200)import qualified Data.ByteString.Lazy.Char8 as Lapp :: Applicationapp _ respond = respond $ responseLBS    status200    [("Content-Type", "text/plain")]    "Hello, World!"main :: IO ()main = do    putStrLn "Starting server on port 8080..."    run 8080 app